<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	//	// Конструктор.	//	function __construct()	{			}		protected function before()	{		$this->title = 'Блог Тони';		$this->content = '';	}		//	// Генерация базового шаблона	//		public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->Template('view/v_main.php', $vars);						echo $page;	}		// Если вызвали метод, которого нет - завершаем работу	public function __call($name, $params){    $this->title .= '::Ошибка';	$error="Ошибка 404!";    $this->content = $this->Template('view/v_error.php', array('error' => $error));	}}