<?php////Контроллер отображения статей//class C_Page extends C_Base {	//###########################################################################################public function action_index(){ //Просмотр главной страницы сайта //GOOD	$this->title .= '::Главная';	$M_Article=M_Article::getInstance();	$articles =$M_Article->articles_intro();	$this->content = $this->Template('view/v_index.php', array('articles' => $articles));	}//############################################################################################public function action_article(){ //Просмотр отдельно взятой статьи //GOOD	$M_Article=M_Article::getInstance();    if(isset($_GET['id']) && $M_Article->articles_get($_GET['id']) && (int)$_GET['id']){    // Информация для отображения.    $M_Comment=M_Comment::getInstance();    $this->title .= '::Просмотр статьи';	$id_article=$_GET['id'];	$info=array();	$info=$M_Article->articles_get($id_article);	$name=$info[0]['title'];	$text=$info[0]['content'];	$comments=$M_Comment->comment_get($id_article);	if($this->isPost())	{		var_dump($_POST);		if(isset($_POST['add']))		{			if($_POST['user_name']==="" && $_POST['comment']===""){ 			echo "Заполните все поля!";			}			else{			$M_Comment->comment_add($id_article, $_POST['user_name'], $_POST['comment']);			header("Location:index.php?c=article&act=article&id=$id_article");			}		}	}	$this->content = $this->Template('view/v_article.php', array('comments' => $comments ,'name' => $name, 'text' => $text));	}	else{	$this->title .= '::Ошибка';	$error="Ошибка 404!";    $this->content = $this->Template('view/v_error.php', array('error' => $error));	}			}//############################################################################################}